mapper.update:
UPDATE
    tbl_report
SET
    processing_text = #{ processingText }
WHERE
    report_code = #{ reportCode }

update
   tbl_member
SET
    member_status = #{ memberStatus }
WHERE
    member_id = #{ memberId }

mapper.select:
SELECT report_code, manager_code, substring(report_text ,10) report_text, report_date, processing_text, processing_date
FROM tbl_report
ORDER BY report_code DESC LIMIT #{offset}, #{limit}

SELECT r.report_code, r.manager_code, c.r_category_name, r.report_text, r.report_date, m.member_id, r.report_status, r.processing_date, r.processing_text
FROM tbl_report r JOIN tbl_member m ON r.target_code = m.member_code JOIN tbl_r_category c ON c.r_category_code = r.r_category_code
WHERE report_code = #{ reportCode }

SELECT member_code, member_id, member_birth, member_status, member_Authority, is_deleted
FROM tbl_member
ORDER BY member_code ASC LIMIT #{offset}, #{limit}

SELECT member_code, member_id, member_birth, member_status, member_Authority, is_deleted
FROM tbl_member
WHERE member_code = #{ memberCode }
